
public class ArrayBasic3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//배열(Array)
		//같은 타입의 여러 변수를 하나의 묶음으로 다루는 것
		
		System.out.println("===========================");
		int[] numArr=new int[5]; //자바

		for(int i=0;i<5;i++) {
			numArr[i]=(i+1)*2;
			System.out.print(numArr[i]+"\t");
		}

		
		
		
	
	
		
	}

}
