package str.exam;

public class StringEx5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 절대 원본 데이터를 변경하지 않는다
		String fileFullName = "Hello.java"; // 기준
		String fileName = "";   
		String fileExtensionName = "";  // 파일 확장자명
		
		String[] fileFullNamerarr= fileFullName.split("\\.");
		
		
		
		fileName = fileFullNamerarr[0];
		fileExtensionName = fileFullNamerarr[1];
		
		
//		파일명과 파일확장자를 출력하시오
// 		ex:  
//		파일 전체명: Hello.java
//		파일명: Hello 
//		확장자명: java
		
		System.out.println("파일 전체명:"+ fileFullName);
		System.out.println("파일명: " + fileName);
		System.out.println("확장자명: " + fileExtensionName);

	}

}
